ï»¿import styled from 'styled-components'

export const Wrapper = styled.div`
  width: 100%;
  max-width: 1120px;
  height: 100vh;
  margin: 0 auto;
  display: flex;
  margin-top: 30px;
  align-items: center;
  display: flex;
  flex-direction: column;
  justify-content: center;

  h2 {
    margin-top: 20px;
    font-size: 2rem;
  }

  export const ImputQuestion = styled.

  input {
    align-items: center;
    width: 900px;
    height: 35px;
    border-radius: 5px;
    margin: 10px;
    font-size: 1.6rem;
    padding: 10px;
  }

  li {
    list-style-type: none;
  }
`

export const AnswerWrapper = styled.div`
  margin-top: 20px;

  .correct {
    font-size: 2rem;
  }
`

export const OptionWrapper = styled.div`
  display: flex;
  max-width: 500px;
`

export const OptionInput = styled.div`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  font-size: 1rem;
`

export const Button = styled.button`
  width: 90px;
  height: 30px;
  background-color: #e0445b;
  color: #fff;
  font-size: 1.3rem;
  font-weight: bold;
  border: 0 solid;
  border-radius: 5px;
  align-items: center;
  margin: 10px 10px 35px 10px;
`
