ï»¿import styled from 'styled-components'

export const Wrapper = styled.section`
  width: 100%;
  max-width: 1120px;
  height: 100vh;
  margin: 0 auto;
  display: flex;
  margin-top: 30px;
  align-items: center;
  display: flex;

  form {
    width: 100%;
    max-width: 900px;
    justify-content: center;
    align-items: center;
  }

  label {
    align-items: left;
  }

  input {
    align-items: center;
    width: 100%;
    height: 30px;
    border-radius: 5px;
    margin: 5px;
  }

  li {
    list-style-type: none;
  }
`

export const Button = styled.button`
  width: 90px;
  height: 30px;
  background-color: #e0445b;
  color: #fff;
  font-size: 1.3rem;
  font-weight: bold;
  border: 0 solid;
  border-radius: 5px;
  align-items: center;
  margin: 10px 10px 35px 10px;
`
