ï»¿import styled from 'styled-components'

export const ButtonsWrapper = styled.div`
  width:  100%;
  height: 100%;
  display: flex;
  margin-top: 60px;
`
export const Buttons = styled.button`
  width: 300px;
  height: 50px;
  background-color: #e0445b;
  color: #fff;
  font-size: 1.5rem;
  border: 0 solid;
  border-radius: 15px;
  margin: 0 40px;
  cursor: pointer;
  transition: 0.5s;

  &:hover {
    background-color: #9e3143;
  }
`
