export const Wrapper = styled.main`
  background-color: #221c41;
  color: #fff;
  width: 100%;
  height: 100%;
  padding: 3rem;
  text-align: center;
  align-items: center;
  justify-content: top;
  margin-top: 1-rem
  display: flex;
  flex-direction: column;
`
export const Title = styled.h1`
  font-size: 2.5rem;
`
export const Description = styled.h2`
  padding-top: 10px;
  font-size: 2rem;
  font-weight: 400;
  color: #e0445b;
`
