﻿import React, { useEffect, useState, Suspense } from 'react'
import fetchGraphQl from '../../service/fetchGraphQl'

function App() {
  const [listQuestions, setListQuestions] = useState([])
  const [questionID, setQuestionID] = useState('')

  const [question, setQuestion] = useState('')
  const [option1, setOption1] = useState('')
  const [option2, setOption2] = useState('')
  const [option3, setOption3] = useState('')
  const [option4, setOption4] = useState('')
  const [answerID, setAnswerID] = useState('1')

  const [reload, setReload] = useState(0)

  //Requisição ao servidor com graphql
  useEffect(() => {
    async function getQuestions() {
      const query = await fetchGraphQl(`
        query questions {
          questions{
            id
            question
            options
            answerID
          }
        }
      `)

      const { questions } = query.data
      setListQuestions(questions)
    }

    getQuestions()
  }, [fetchGraphQl, reload])

  async function getQuestion(id) {
    setQuestionID(id)

    const query = await fetchGraphQl(
      `
      query question($id: ID!) {
        question (id: $id){
          id
          question
          options
          answerID
        }
      }
    `,
      { id: id }
    )

    if (query.data) {
      const { question } = query.data

      setQuestion(question.question)
      setAnswerID(question.answerID)
      setOption1(question.options[0])
      setOption2(question.options[1])
      setOption3(question.options[2])
      setOption4(question.options[3])
    }
  }

  async function createQuestion(event) {
    event.preventDefault()

    const opt = [option1, option2, option3, option4]
    const query = await fetchGraphQl(
      `
    mutation createQuestion($question: String, $options: [String], $answerID: String) {
      createQuestion (
        question: $question
        options: $options
        answerID: $answerID
      ){
        id
        question
        options
        answerID
      }
    }
  `,
      { question: question, options: opt, answerID: answerID + '' }
    )

    if (query.data) {
      success('Nova questão cadastrada com sucesso.')
    }
  }

  async function updateQuestion(event: Event) {
    event.preventDefault()

    const opt = [option1, option2, option3, option4]
    const query = await fetchGraphQl(
      `
    mutation updateQuestion($id: ID, $question: String, $options: [String], $answerID: String) {
      updateQuestion (
        id: $id
        question: $question
        options: $options
        answerID: $answerID
      ){
        id
        question
        options
        answerID
      }
    }
  `,
      {
        id: questionID,
        question: question,
        options: opt,
        answerID: answerID + ''
      }
    )

    if (query.data) {
      success('Questão atualizada com sucesso.')
    }
  }

  async function deleteQuestion(id: string) {
    console.log('deletar')

    const query = await fetchGraphQl(
      `
    mutation deleteQuestion($id: ID) {
      deleteQuestion (
        id: $id
      ){
        id
      }
    }
  `,
      { id: id }
    )

    if (query.data) {
      const { deleteQuestion } = query.data

      if (deleteQuestion === null) {
        success('Questão excluída com sucesso.')
      }
    }
  }

  function success(text, ctrl = true) {
    if (ctrl) {
      alert(text)
      setReload(reload + 1)
      setQuestionID('1')
    }

    setQuestion('')
    setOption1('')
    setOption2('')
    setOption3('')
    setOption4('')
    setAnswerID('')
  }

  function answerOpt(event) {
    setAnswerID(event.target.value)
  }

  return (
    <div className="App">
      <div id="list-question" className="box-div">
        <h2>Perguntas Cadastradas</h2>
        <ul id="question-ul">
          {listQuestions.map((quest, index) => (
            <li key={quest.id} className={index % 2 == 0 ? 'separator' : ''}>
              <div>
                {' '}
                <span className="question-ul-number">{index + 1}</span>{' '}
                <span>{quest.question.substring(0, 80)}</span>{' '}
              </div>
              <div>
                <button
                  className="btn-edit"
                  onClick={() => getQuestion(quest.id)}
                >
                  EDITAR
                </button>
                <button
                  className="btn-del"
                  onClick={() => deleteQuestion(quest.id)}
                >
                  EXCLUIR
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>

      <form
        id="form-question"
        className="box-div"
        onSubmit={questionID === '' ? createQuestion : updateQuestion}
      >
        <h2>{questionID === '' ? 'Nova Pergunta' : 'Editar Pergunta'}</h2>

        <div id="question-input">
          <label htmlFor="questionInput">Pergunta</label>
          <input
            required
            id="questionInput"
            type="text"
            value={question}
            onChange={(event) => setQuestion(event.target.value)}
          />
        </div>

        <ul id="question-ul">
          <li>
            <label htmlFor="questionOption_1">Resposta 1</label>
            <input
              required
              id="questionOption_1"
              type="text"
              value={option1}
              onChange={(event) => setOption1(event.target.value)}
            />
          </li>

          <li>
            <label htmlFor="questionOption_2">Resposta 2</label>
            <input
              required
              id="questionOption_2"
              type="text"
              value={option2}
              onChange={(event) => setOption2(event.target.value)}
            />
          </li>

          <li>
            <label htmlFor="questionOption_3">Resposta 3</label>
            <input
              required
              id="questionOption_3"
              type="text"
              value={option3}
              onChange={(event) => setOption3(event.target.value)}
            />
          </li>

          <li>
            <label htmlFor="questionOption_4">Resposta 4</label>
            <input
              required
              id="questionOption_4"
              type="text"
              value={option4}
              onChange={(event) => setOption4(event.target.value)}
            />
          </li>
        </ul>

        <div>
          <label htmlFor="">Resposta correta: {answerID}</label>
          <div id="question-answer" onChange={(event) => answerOpt(event)}>
            <div>
              <input
